mapper.select:
SELECT pt.id, category_id, i_dol_id, name, price, stock, image_url
FROM product p
JOIN (SELECT id
    FROM product

        AND category_id = #{categoryId}
        AND i_dol_id = #{iDolId}



            ORDER BY price DESC


            ORDER BY price ASC


            ORDER BY id DESC


    LIMIT #{size}
    OFFSET #{offset}
) pt ON pt.id = p.id;

SELECT COUNT(*)
FROM product

    AND category_id = #{categoryId}
    AND i_dol_id = #{iDolId}
